// Obtener el primer elemento de los datos de entrada
const input = items[0];

// Verificar si 'input' y 'input.json' están definidos
if (input && input.json && input.json.body && input.json.body.combinations) {
    // Obtener el objeto 'combinations'
    const combinations = input.json.body.combinations;

    // Inicializar un arreglo para almacenar los resultados
    const fieldsArray = [];


    // Iterar sobre las propiedades del objeto 'combinations'
    for (const key in combinations) {
        if (combinations.hasOwnProperty(key)) {
            // Acceder a cada combinación usando su clave
            const combination = combinations[key];

            // Añadir la combinación al arreglo de fields
            fieldsArray.push({
                id_product_attribute: combination.id_product_attribute,
                attributes: combination.attributes,
                price: combination.price,
                quantity: combination.quantity,
                image:
combination.images
            });
        }
    }

    // Retornar un solo objeto con el campo 'fields' que contiene el arreglo de combinaciones
    return [{
        json: {
            fields: fieldsArray
        }
    }];
}

// Si no se encuentran combinaciones, retornar los items originales
