let price = items[0].json.PrestaShop_Body.body.price;
let PrestaShop_Body = items[0].json;

// Check if the name field is in object format with an index "1"
if (typeof items[0].json.PrestaShop_Body.body.name === 'object' && items[0].json.PrestaShop_Body.body.name.hasOwnProperty("1")) {
    // Extract the value from index "1"
    name = items[0].json.PrestaShop_Body.body.name["1"];
} else if (typeof items[0].json.PrestaShop_Body.body.name === 'string') {
    // If it's already in the desired format, assign the value directly
    name = items[0].json.PrestaShop_Body.body.name;
}

// Check if the description_short field is in object format with an index "1"
if (typeof items[0].json.PrestaShop_Body.body.description_short === 'object' && items[0].json.PrestaShop_Body.body.description_short.hasOwnProperty("1")) {
    // Extract the value from index "1"
    description_short = items[0].json.PrestaShop_Body.body.description_short["1"];
} else if (typeof items[0].json.PrestaShop_Body.body.description_short === 'string') {
    // If it's already in the desired format, assign the value directly
