// Extraemos las variantes y las variaciones del JSON de entrada
const variants = items[0].json.data.variants;
const variations = items[0].json.WooCommerce_Body.body.variations;

// Inicializamos los arrays que contendrán los IDs de las variaciones coincidentes y las variantes no coincidentes
let matchingVariants = [];
let nonMatchingVariants = [];

// Iteramos sobre las variantes para verificar si el remote_id coincide con alguna variación
variants.forEach(variant => {
  const remoteIdAsNumber = Number(variant.remote_id);

  // Verificamos si el remote_id de la variante está en el array de variations
  if (variations.includes(remoteIdAsNumber)) {
    matchingVariants.push(remoteIdAsNumber); // Agregamos a matchingVariants
  } else {
    nonMatchingVariants.push(variant); // Agregamos la variante completa a nonMatchingVariants
  }
});

// Retornamos el resultado como salida del nodo
return [
  {
    json: {
      matchingVariants,
      nonMatchingVariants
    }
  }
];

// Obtener el primer elemento de los datos de entrada
const input = items[0];

// Verificar si 'input' y la estructura de variantes existen
if (input && input.json && input.json.data && input.json.data.variants) {

    // Obtener la lista de variantes
    const variants = input.json.data.variants;

    // Filtrar las variantes activas
    const activeVariantsCount = variants.filter(v => v.status.name !== 'Inactive' && v.status.name !== 'Archived' ).length;

    // Retornar el conteo de variantes activas
    return [{
        json: {
            activeVariantsCount: activeVariantsCount // Conteo de variantes activas
        }
    }];
}

// Si no se encuentran variantes, retornar los items originales
return items;

