const input = items[0];

// Verificar si 'input' y 'input.json' están definidos
if (input && input.json) {
    // Obtener los datos de 'fields' y 'products_variants'
    const fields = input.json.fields || [];
    const productsVariants = input.json.data.products_variants || [];

    // Crear un conjunto de todos los remote_id en products_variants
    const remoteIds = new Set(productsVariants.map(variant => variant.remote_id));

    // Crear un array para almacenar todas las combinaciones no coincidentes
    const nonMatchingCombinations = [];

    // Recorrer todos los fields
    fields.forEach(field => {
        // Verificar si 'id_product_attribute' no coincide con ningún 'remote_id'
        const idMatch = !remoteIds.has(field.id_product_attribute.toString());

        // Si no coincide, agregar al array de no coincidentes
        if (idMatch) {
            nonMatchingCombinations.push({
                id_product_attribute: field.id_product_attribute,
                attributes: field.attributes,
                price: field.price,
                quantity: field.quantity,
                image: field.image || [],
            });
        }
    });

    // Devolver todas las combinaciones no coincidentes
    if (nonMatchingCombinations.length > 0) {
        return [{
            json: {
                new_combinations: nonMatchingCombinations
            }
        }];
    }
}

// Si no se encuentran coincidencias, retornar los items originales
return items;
